{% block title %}Nos Produits - L'Espace Vert en Ligne {% endblock %}

{% block content %}
    <h1>
        NOS PRODUITS  
    </h1>

    <div class=\"row product-container\">
        {% for product in products %}
            <div class=\"col-md-4\">
                <div class=\"product-item text-center\">
                    <a href=\"{{ path('product', {'slug': product.slug} )}}\"><img src=\"/upload/images/products/{{ product.illustration }}\" alt=\"{{ product.name }}\" class=\"img-fluid\"></a>
                    <h5> {{ product.name }} </h5>
                    <span class=\"product-subtitle\"> {{ product.subtitle }} </span>
                    <span class=\"product-price\"> {{ (product.price / 100)|number_format(2,',','.') }} €</span>
                    
                </div>
            </div>
        {% endfor %}
    </div>
{% endblock %}
